import React, { useState } from "react";
import "../../styles/recipes.css";

const Recipes = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setError("Please enter a recipe name.");
      return;
    }

    try {
      const response = await fetch(`http://127.0.0.1:5000/search-recipes?name=${searchQuery}`);
      const data = await response.json();

      if (data.error) {
        setError(data.error);
        setRecipes([]);
      } else {
        setRecipes(data.recipes);
        setError("");
      }
    } catch (err) {
      setError("Error fetching recipes. Please try again.");
      setRecipes([]);
    }
  };

  return (
    <div className="search-container"> {/* Added class */}
      <h2>Search for Recipes</h2>
      <input
        type="text"
        placeholder="Enter recipe name..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        style={{ padding: "10px", width: "300px", marginRight: "10px" }}
      />
      <button onClick={handleSearch} style={{ padding: "10px 20px", cursor: "pointer" }}>
        Search
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <div style={{ marginTop: "20px" }}>
        {recipes.length > 0 ? (
          <ul>
            {recipes.map((recipe, index) => (
              <li key={index}>
                <h3>{recipe.name}</h3>
                <p><strong>Ingredients:</strong> {recipe.ingredients}</p>
                <p><strong>Description:</strong> {recipe.instructions}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No recipes found.</p>
        )}
      </div>
    </div>
  );
};

export default Recipes;
