import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const SignUp = () => {
  const [credentials, setCredentials] = useState({
    name: "",
    phone: "",
    email: "",
    password: "",
  });

  const [loading, setLoading] = useState(false); // ✅ Add loading state
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // ✅ Disable button while submitting
    console.log("Submitting Sign-Up Form...");

    try {
      const response = await axios.post("http://127.0.0.1:5000/signup", credentials);
      console.log("Response:", response.data);
      alert("Sign-up successful!");
      navigate("/login"); // ✅ Redirect after successful sign-up
    } catch (error) {
      console.error("Sign-Up Error:", error);
      alert("Sign-up failed!");
    } finally {
      setLoading(false); // ✅ Re-enable button after response
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h2>Sign Up</h2>
        <input type="text" name="name" placeholder="Full Name" onChange={handleChange} required />
        <input type="tel" name="phone" placeholder="Phone Number" onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
        <button type="submit">Sign Up</button>

        {/* ✅ Disable button when submitting */}
        <button type="submit" disabled={loading}>
          {loading ? "Signing Up..." : "Sign Up"}
        </button>
      </form>
    </div>
  );
};

export default SignUp;
